mapper.insert:

	SELECT FILES_SEQ.NEXTVAL
	FROM DUAL

INSERT INTO files (
files_no
, job_no
, user_id
, upload_date
) VALUES (
#{filesNo}
, #{jobId}
, #{userId}
, SYSDATE
)

INSERT INTO files_details (
details_no
, files_no
, files_name
, files_size
, files_type
, files_path
, files_uuid
)

	SELECT
	FILES_DETAILS_SEQ.NEXTVAL
	, #{file.filesNo}
	, #{file.filesName}
	, #{file.filesSize}
	, #{file.filesType}
	, #{file.filesPath}
	, #{file.filesUuid}
	FROM DUAL
