mapper.select:
SELECT we.work_entries_no , we.workers , we.work_date , we.work_time , we.work_content , we.job_id , j.title , we.work_details_no , wd.work_type FROM work_entries we JOIN job j ON we.job_id = j.job_no LEFT JOIN work_details wd ON we.work_details_no = wd.work_details_no
AND we.job_id = #{jobId}
AND j.title LIKE '%' || #{title} || '%'
AND we.workers = #{workers}
AND we.work_date = #{workDate}
AND we.work_details_no = #{workDetailsNo}
AND we.work_content LIKE '%' || #{workContent} || '%'
ORDER BY we.work_entries_no DESC

mapper.insert:
INSERT INTO work_entries
(
work_entries_no
, workers
, work_date
, work_time
, work_content
, job_id
, work_details_no
)
VALUES
(
work_entries_seq.NEXTVAL
, #{managerId}
, #{workDate}
, #{workTime}
, #{workContent}
, #{jobId}
, #{workDetailsNo}
)
